
public class ReCap {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int numOfItems = 5;
		System.out.println(3.0 / 1.5);
		System.out.println(3.0 / 2);// 2 will automatically convert to double value
		System.out.println((numOfItems + 10) / 2); // () is for order operation. the result will be 7
		System.out.println((numOfItems + 10) / 2.0); // return 7.5
	}

}
